/* For urls starting with:  https://news.ycombinator.com */
body
{
    background-color: #f6f6ef;
}

#hnmain
{
    width: 100%;
    padding-left: 10%;
    margin-top: -10px;
    margin-left: -10px;
    padding-right: 10%;
}

td
{
    background-color: #f6f6ef;
}


.itemlist, .title, .sitestr, .comment, .comhead .age, .comhead .hnuser, .togg
{
    font-size: 2.5em !important;
}

.comment
{
    padding-bottom: 5%;
}

.subtext
{
    font-size: 1em !important;
}

.fatitem form, .reply, .votearrow, .pagetop
{
    display: none;
}

a img
{
    filter: grayscale(100%);
    position: absolute;
    top: 1em;
    left: 1em;
}

.comment-tree .votelinks
{
    border-left: 1em solid #828282;
}

.comment-tree
{
    margin-top: -30px;
}

.athing
{
    margin-top: -10px;
}

.hnmain
{
    margin-top: -50px;
}

/* 
For URL: https://news.ycombinator.com/
For URL matching regexps: https:\/\/news\.ycombinator\.com\/news.*
*/

.storylink, .title
{
    font-size: 1.2em !important;
}
